const state = () => ({
  stats: {
    students: 243,
    courses: 13,
    paymentsLabel: 'INR',
    payments: 556000,
    users: 3
  },
  users: [
    {
      name: 'Karthi', email: 'karthi@gmmail.com', phone: '7305477760',
      enroll: '1234567305477760', date: '08-Dec, 2021',
      avatar: require('../../assets/avatar.jpg')
    },
    { name: 'Karthi', email: 'karthi@gmmail.com', phone: '7305477760', enroll: '1234567305477760', date: '08-Dec, 2021', avatar: require('../../assets/avatar.jpg') },
    { name: 'Karthi', email: 'karthi@gmmail.com', phone: '7305477760', enroll: '1234567305477760', date: '08-Dec, 2021', avatar: require('../../assets/avatar.jpg') },
    { name: 'Karthi', email: 'karthi@gmmail.com', phone: '7305477760', enroll: '1234567305477760', date: '08-Dec, 2021', avatar: require('../../assets/avatar.jpg') }
  ]
})

const getters = {
  stats: (s) => s.stats,
  users: (s) => s.users
}

const actions = {
  addUser({ state }, user) { state.users.unshift(user) },
  updateUser({ state }, { index, user }) { state.users.splice(index, 1, user) },
  deleteUser({ state }, index) { state.users.splice(index, 1) }
}

export default { namespaced: true, state, getters, actions }
